                        if (target.is('.app-btn-toggle,[data-href]'))
                            return;
                        //fieldContainer = target.closest('.app-field');
                        //if (fieldContainer.length) {
                        //    hyperlink = fieldContainer.find('[data-href]');
                        //    if (hyperlink.length) {
                        //        setTimeout(function () {
                        //            hyperlink.trigger('vclick');
